E2Helper.Descriptions["smithGet"] = "Returns exterior shell of a smith when used on an occupant, the exterior itself, or the interior."
E2Helper.Descriptions["smithDemat"] = "Initiates dematerialisation, takes vector pos and optional angle rot, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithPhase"] = "Toggles phase, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithFlightmode"] = "Toggles flightmode, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithLock"] = "Locks it, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithPhyslock"] = "Toggles physlock, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithPower"] = "Toggles power, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithIsomorph"] = "Toggles isomorphic security systems, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithLongflight"] = "Toggles long-flight, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithMaterialise"] = "Rematerialises, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithSelfrepair"] = "Toggles self-repair, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithTrack"] = "Sets tracking entity, takes entity ent, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithSpinmode"] = "Sets spinmode, takes number spinmode (-1=anti-clockwise|0=none|1=clockwise), returns spinmode as number."
E2Helper.Descriptions["smithSetDestination"] = "Sets destination if in vortex, takes vector pos and optional angle rot, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithFastReturn"] = "Returns smith to its previous takeoff position, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithHADS"] = "Toggles HADS, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithFastDemat"] = "Quickly dematerialises smith, returns 1 if success or 0 if failure."
E2Helper.Descriptions["smithMoving"] = "Returns 1 if teleporting or 0 if not teleporting."
E2Helper.Descriptions["smithVisible"] = "Returns 1 if visible or 0 if invisible."
E2Helper.Descriptions["smithFlying"] = "Returns 1 if flying or 0 if not flying."
E2Helper.Descriptions["smithLocked"] = "Returns 1 if locked or 0 if not locked."
E2Helper.Descriptions["smithPhyslocked"] = "Returns 1 if physlocked or 0 if not physlocked."
E2Helper.Descriptions["smithPowered"] = "Returns 1 if powered or 0 if not powered."
E2Helper.Descriptions["smithIsomorphic"] = "Returns 1 if isomorphic security systems are enabled or 0 if isomorphic security systems are disabled."
E2Helper.Descriptions["smithLongflighted"] = "Returns 1 if long-flight is enabled or 0 if long-flight is disabled."
E2Helper.Descriptions["smithSelfrepairing"] = "Returns 1 if self-repairing or 0 if not self-repairing."
E2Helper.Descriptions["smithHealth"] = "Returns smith health."
E2Helper.Descriptions["smithLastPos"] = "Returns the vector of where the smith last dematerialised from."
E2Helper.Descriptions["smithLastAng"] = "Returns the angle of where the smith last dematerialised from."
E2Helper.Descriptions["smithTracking"] = "Returns the entity the smith is tracking."
E2Helper.Descriptions["smithInVortex"] = "Returns 1 if in vortex, or 0 if not."
E2Helper.Descriptions["smithIsHADS"] = "Returns 1 if HADS is enabled, or 0 if not."
E2Helper.Descriptions["smithPilot"] = "Returns the pilot if valid."